%sh
BASE=/user/zeppelin/cms_hospital_compare

hdfs dfs -rm -r -skipTrash ${BASE}
hdfs dfs -mkdir ${BASE}

cd ~/cms_hospital_compare/data

ls *.dat | 
while read f; do  
  fn=`echo ${f} | tr " -" "_" | sed -e "s/__*/_/g" -e "s/\.dat//" | tr "[A-Z]" "[a-z]" `
  hdfs dfs -mkdir -p ${BASE}/current/${fn}
  hdfs dfs -put ${f} ${BASE}/current/${fn}
done

hdfs dfs -chmod -R 777 ${BASE}
